// wap to input a number and print number is even or odd till user input a neg num.
 #include<stdio.h>
void main()
{ 
int n;
start:
printf("Enter a number :");
scanf("%d",&n);
if(n<0)
goto end;
if(n%2==0)
 printf("Even\n");
else
	printf("odd\n");
goto start;	
end:
	printf("###########################");
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               